mapper.select:
SELECT @ROWNUM := @ROWNUM +1 AS row, qna_seq, qna_title, qna_contents, qna_cnt, file_nm, file_loc, file_size, enr_user, upd_user, SUBSTRING_INDEX( upd_date, ' ', 1) upd_date, SUBSTRING_INDEX( enr_date, ' ', 1) enr_date FROM tb_qna ,(select @ROWNUM := 0) aa
${searchkey} like '%${searchword}%'
ORDER BY cast(qna_seq as unsigned) LIMIT #{pageIndex}, #{pageSize}

SELECT qna_seq, qna_title, qna_contents, qna_cnt, file_nm, file_loc, file_size, enr_user, upd_user, SUBSTRING_INDEX( upd_date, ' ', 1) upd_date, SUBSTRING_INDEX( enr_date, ' ', 1) enr_date FROM tb_qna
${searchkey} like '%${searchword}%'
ORDER BY cast(qna_seq as unsigned) LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
     insert into tb_qna (
		   qna_seq,qna_title,qna_contents,qna_cnt,file_nm,file_loc,file_size,enr_user,enr_date,upd_user,upd_date
		)select
		  COALESCE(MAX(cast(qna_seq as unsigned))+1,1)
		  ,#{qna_title}  
		  ,#{qna_contents}   
		  ,0    
		  ,#{fileInfo.file_nm}   
		  ,#{fileInfo.file_loc}  
		  ,#{fileInfo.file_size}   
		  ,#{enr_user}   
		  ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' )  
		  ,#{upd_user}   
		  ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' )   
		from tb_qna

mapper.update:
update tb_qna SET
   qna_title = #{qna_title} 
  ,qna_contents =  concat(DATE_FORMAT(NOW(),'%Y-%c-%d %T' ),'- 수정됨 \n',#{qna_contents}) 
  ,file_nm = #{file_nm} 
  ,file_loc = #{file_loc}  
  ,file_size = #{file_size}  
  ,upd_user = #{loginID} 
  ,upd_date = DATE_FORMAT(NOW(),'%Y-%c-%d %T' )  
WHERE qna_seq = #{qna_seq}
