mapper.select:
/*kr.kosmo.jobkorea.mpm.dao.MpmDao.selectStudntList*/ SELECT loginID ,password ,name ,user_type ,user_email ,user_addr ,addr_detail ,user_post ,user_joinDate ,status_yn ,regUserId FROM tb_userinfo where 1=1 and status_yn = 'Y'
and user_type =#{user_type}
and name like CONCAT('%', #{searchTxt}, '%')
and user_joinDate >= DATE_FORMAT('${fromDt} 00:00:00','%y-%m-%d %H:%i:%S')
and user_joinDate <= DATE_FORMAT('${toDt} 23:59:59','%y-%m-%d %H:%i:%S')
order by user_joinDate desc

SELECT loginID ,password ,name ,user_type ,user_email ,user_addr ,addr_detail ,user_post ,user_joinDate ,status_yn ,regUserId ,file_nm as fileNm ,concat(file_path,'/',file_nm) as filePath FROM tb_userinfo where 1=1 and status_yn = 'Y'
and user_type =#{user_type}
and loginID =#{loginID}

mapper.insert:
		/*kr.kosmo.jobkorea.system.dao.ComnCodDao.insertComnGrpCod*/
		INSERT INTO tb_userinfo
		(
            loginID,

			password,


			name,


			user_type,


			user_email,


			user_addr,


			addr_detail,


			user_post,


			file_Path,


			file_nm,

			user_joinDate,
			status_yn,
			regUserId
		)
		VALUES
		(
				#{loginID}

			,	#{password}


			,	#{name}


			,	#{user_type}


			,	#{user_email}


			,	#{user_addr}


			,	#{addr_detail}


			,	#{user_post}


			,	#{filePath}


			,	#{fileNm}

			,	NOW()
			,	'Y'

			,	#{regUserId}

		)
