mapper.select:
SELECT tl.lec_seq, tl.lec_nm, a.loginID, a.user_email, a.user_addr, tc.cns_cstee, tc.cns_seq
FROM tb_lecture tl JOIN tb_request_list rl ON rl.lec_seq =#{lec_seq}, (SELECT loginID, user_email, user_addr FROM tb_userinfo tu, tb_request_list rl WHERE tu.loginID = rl.user_id )a LEFT JOIN tb_counsel tc ON a.loginID = tc.cns_cstee WHERE tl.lec_seq =#{lec_seq} AND a.loginID = rl.user_id GROUP BY loginID, user_email, user_addr, cns_cstee
LIMIT #{pageIndex}, #{pageSize}
