mapper.insert:
insert into tb_survey  (
   lec_seq,sv_seq ,sv_score,sv_comments,
   enr_user,enr_date,upd_user,upd_date
) SELECT 
 #{lec_seq}   
  ,ifnull(Max(cast(sv_seq as unsigned))+1,1)
  ,#{totalScore}  
  ,#{comment} 
  ,#{enr_user} 
  ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' )   
  ,#{enr_user}  
  ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' )  
FROM tb_survey
where lec_seq=#{lec_seq};
