mapper.insert:

/*kr.kosmo.jobkorea.lsm.dao.LsmDao.insertLsmCod*/
INSERT INTO tb_task
(
	task_seq, lec_seq, task_nm, enr_user, task_cont, enr_date 
)
VALUES
(


			 task_seq = '1' 


			(SELECT IFNULL(MAX(CAST(b.task_seq AS UNSIGNED)),0) + 1 FROM tb_task b WHERE lec_seq = #{lec_seq} )


	,	#{lec_seq}
	,	#{task_nm}
	,	#{enr_user}
	,   #{task_cont}
	,	now()
)

mapper.update:

/*kr.kosmo.jobkorea.lsm.dao.LsmDao.updateLsmCod*/
UPDATE tb_task SET
		lec_seq = #{lec_seq}
	,	task_nm = #{task_nm}
	,	enr_user = #{enr_user}
	,	task_cont = #{task_cont}
	,	upd_date = NOW()
WHERE task_seq = #{task_seq} and lec_seq=${lec_seq}

mapper.insert:

/* kr.kosmo.jobkorea.lsm.dao.LsmDao.saveLsmFil*/

INSERT INTO tb_task_file
(
   lec_seq, task_seq, fil_seq, lgc_fil_nm, psc_fil_nm, fil_siz, fil_ets
)
VALUES
(
		#{lec_seq}
	,  
		#{task_seq}
	,  
	(
	     	SELECT  IFNULL(MAX(fil_seq),0) +1
	     	FROM tb_task_file tf, tb_task tb
	       WHERE tf.lec_seq = tb.lec_seq 
	       AND tf.task_seq = tb.task_seq
	       AND tb.lec_seq = #{lec_seq} AND tb.task_seq = #{task_seq}
		)			
	,	#{lgc_fil_nm}
	,	#{psc_fil_nm}
	,	#{fil_siz}
	,	#{fil_ets}
)

mapper.select:
/* kr.kosmo.jobkorea.lsm.dao.lsmBoardDao.selectLsmFil*/
SELECT	task_seq
	,	fil_seq as snm
	,	lgc_fil_nm
	, 	psc_fil_nm
	,	 fil_siz
	, 	fil_ets
FROM		tb_task_file
WHERE	task_seq = #{task_seq}
	AND fil_seq = #{snm}

/* kr.kosmo.jobkorea.lsm.dao.lsmBoardDao.selectStuFil*/
SELECT	task_seq
	,	fil_seq as snm
	,	lgc_fil_nm
	, 	psc_fil_nm
	,	 fil_siz
	, 	fil_ets
FROM		tb_task_file
WHERE	task_seq = #{task_seq}
	AND lec_seq = #{lec_seq}

mapper.update:
/* kr.kosmo.jobkorea.lsm.dao.lsmBoardDao.updateLsmFil*/
UPDATE tb_task_file SET
		lgc_fil_nm = #{lgc_fil_nm}
	, 	psc_fil_nm = #{psc_fil_nm}
	,	 fil_siz = #{fil_siz}
	, 	fil_ets = #{fil_ets}
WHERE task_seq = #{task_seq}
	AND fil_seq =#{snm}

mapper.delete:
		/* kr.kosmo.jobkorea.lsm.dao.lsmBoardDao.deleteLsmFil*/
		DELETE FROM tb_task_file
		WHERE fil_seq = #{fil_seq}

			AND fil_seq IN

            			#{item}

mapper.select:

/*kr.kosmo.jobkorea.lsm.dao.LSMBoardDao.countLsmStuListCod*/
SELECT COUNT(1) AS stu_tot_cnt FROM tb_task_send
WHERE lec_seq = #{lec_seq} AND task_seq = #{task_seq}
