mapper.select:
/*kr.kosmo.jobkorea.lss.dao.LSSEnrDao.ListLss*/ SELECT @rownum:=@rownum+1 AS row_num , lec_seq , ldo_seq , lec_title , enr_user , ldo_cont , Date_Format(enr_date, '%Y-%m-%d') AS enr_date , Date_Format(upd_date, '%Y-%m-%d') AS upd_date FROM tb_lecdocu
${searchKey} like '%${searchWord}%'
(@rownum :=0)=0
ORDER BY ldo_seq LIMIT #{pageIndex},#{pageSize}


/*kr.kosmo.jobkorea.lss.dao.LSSEnrDao.selectldo_seq*/
SELECT MAX(ldo_seq) +1
FROM tb_lecdocu
where lec_seq = #{lec_seq}


/*kr.kosmo.jobkorea.lss.dao.LSSBoardDao.selectLecSeq*/
SELECT led.lec_seq
FROM tb_lecdocu led , tb_lecture lec
WHERE led.lec_seq = lec.lec_seq
AND led.enr_user = #{enr_user}
GROUP BY led.lec_seq

mapper.insert:

/*kr.kosmo.jobkorea.lss.dao.LSSEnrDao.insertLecDoc*/
INSERT INTO tb_lecdocu 
(
	lec_seq, ldo_seq, lec_title, enr_user, ldo_cont, enr_date
)
VALUES
(
		#{lec_seq} 
	,	(
			SELECT IFNULL(MAX(b.ldo_seq),0)+1 
			FROM tb_lecdocu b 
			WHERE lec_seq = #{lec_seq}
		)
	,	#{lec_title}
	,	#{enr_user}
	,	#{ldo_cont}
	,	DATE_FORMAT(NOW(), '%Y-%c-%d %T')

)

mapper.update:

/*kr.kosmo.jobkorea.lss.dao.LSSEnrDao.updateLecDoc*/
UPDATE tb_lecdocu SET
		lec_title = #{lec_title}
	,	ldo_cont = #{ldo_cont}
	,	upd_date = Date_Format(NOW(), '%Y-%m-%d')
WHERE ldo_seq = #{ldo_seq}


		/*kr.kosmo.jobkorea.lss.dao.LSSEnrDao.updateLecDocfile*/
		UPDATE tb_lecdocu SET
				lgc_fil_nm = #{lgc_fil_nm}
  			,	psc_fil_nm = #{psc_fil_nm}
			,	fil_siz = #{fil_siz}
			,	fil_ets = #{fil_ets}
		WHERE ldo_seq = #{ldo_seq}
