mapper.select:
SELECT COUNT(*) as total_cnt from ( SELECT rm_seq , rm_name , rm_pper , rm_size FROM tb_room WHERE rm_yon = 'Y'
AND rm_name LIKE concat('%', #{rm_name}, '%')
)a

SELECT rm_seq , rm_name , rm_pper , rm_size , lgc_rm_nm , psc_rm_nm FROM tb_room WHERE rm_yon = 'Y'
AND rm_name LIKE concat('%', #{rm_name}, '%')
ORDER BY rm_seq LIMIT #{pageIndex}, #{pageSize}

mapper.insert:

            SELECT IFNULL(MAX(CAST(rm_seq AS UNSIGNED)),0) + 1 FROM tb_room 

		INSERT INTO tb_room (
		    rm_seq	
		  ,	rm_name								
		  , rm_pper								
		  , rm_size								
		  , enr_user								
		  , enr_date
		  , lgc_rm_nm
		  , psc_rm_nm
		  ,	rmPic_size					
		) VALUES (
		    #{rm_seq}
		  , #{rm_name}
		  , #{rm_pper}
		  , #{rm_size}
		  , #{enr_user}
		  , now()
		  ,	#{lgc_rm_nm}
		  , #{psc_rm_nm}
		  , #{rmPic_size}
		)

mapper.update:
UPDATE 
		tb_room
SET
		rm_name = #{rm_name}
	,	rm_pper	= #{rm_pper}
	,	rm_size = #{rm_size}
	,	upd_user = #{upd_user}
	,	upd_date = now()
	,	lgc_rm_nm = #{lgc_rm_nm}
	,   psc_rm_nm = #{psc_rm_nm}
	,   rmPic_size = #{rmPic_size}
WHERE rm_seq = #{rm_seq}

mapper.insert:

            SELECT IFNULL(MAX(CAST(item_seq AS UNSIGNED)),0) + 1 FROM tb_item WHERE rm_seq = #{rm_seq}

		INSERT INTO tb_item (
			    item_seq
			,	rm_seq
	        ,	item_nm
	        ,	item_vol
	        ,	item_nt
	        ,	enr_user
	        ,	enr_date				
		) VALUES (
		    #{item_seq}
		  , #{rm_seq}
		  , #{item_nm}
		  , #{item_vol}
		  , #{item_nt}
		  , #{enr_user}
		  , now()
		)

mapper.update:
UPDATE 
		tb_item
SET
		item_nm = #{item_nm}
	,	item_vol = #{item_vol}
	,	item_nt = #{item_nt}
	,	upd_user = #{upd_user}
	,	upd_date = now()
WHERE item_seq = #{item_seq}
ORDER BY item_seq
