mapper.select:
SELECT nt_seq, nt_title, nt_contents, nt_cnt, enr_user, enr_date, upd_user, upd_date, file_nm, file_loc, file_size FROM tb_notice
${searchkey} like '%${searchword}%'
ORDER BY nt_seq LIMIT #{pageIndex}, #{pageSize}

mapper.update:
update tb_notice 
set nt_cnt=nt_cnt+1
where nt_seq=#{nt_seq}

mapper.select:
select nt_cnt 
from tb_notice
where nt_seq = #{value}

mapper.insert:
insert into tb_notice_reply ( nt_seq,rpy_seq,rpy_lvl,rpy_contents, enr_user,enr_date,upd_user,upd_date,no ) SELECT #{nt_seq}
,ifnull(MAX(rpy_seq)+1,1) ,0 ,#{replyContent}
,#{rpy_seq} ,1 ,LTRIM(replace(#{replyContent},subSTring_index(#{replyContent},' ',1),''))
,#{enr_user} ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' ) ,#{upd_user} ,DATE_FORMAT(NOW(),'%Y-%c-%d %T' ) ,ifnull(MAX(no)+1,1) FROM tb_notice_reply

mapper.update:
update tb_notice_reply 
set rpy_contents ='삭제된 내용입니다.',action='d'
where no=#{value}


update  tb_notice SET
  nt_title = #{nt_title}
  ,nt_contents = #{nt_contents}
  ,upd_user = #{upd_user}
  ,upd_date = DATE_FORMAT(NOW(),'%Y-%c-%d %T' )    
  ,file_nm = #{fileInfo.file_nm}
  ,file_loc = #{fileInfo.file_loc}
  ,file_size = #{fileInfo.file_size}
WHERE nt_seq = #{nt_seq}
