mapper.select:
/*kr.kosmo.jobkorea.sss.dao.SSSLecDao.ListSss*/ SELECT @rownum:=@rownum+1 AS row_num , lec_seq , ldo_seq , lec_title , enr_user , ldo_cont , Date_Format(enr_date, '%Y-%m-%d') AS enr_date , Date_Format(upd_date, '%Y-%m-%d') AS upd_date FROM tb_lecdocu
